import React, { useState, useEffect, useRef, useCallback } from 'react';
import './index.css';

// --- CONFIGURATION CONSTANTS ---
const MAX_ROUNDS = 15;
const MAX_TIME = 120;
const WORK_INCREMENT = 15;
const REST_INCREMENT = 5;
const PREP_TIME = 5;

// --- WORKOUT STATE ENUM ---
const WORKOUT_STATES = {
    SETUP: 'SETUP',
    PREP: 'PREP',
    WORK: 'WORK',
    REST: 'REST',
    COMPLETE: 'COMPLETE'
};

// --- HELPER FUNCTIONS ---

const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${String(minutes).padStart(2, '0')}:${String(secs).padStart(2, '0')}`;
};

// Function to play the sound
const playHighPitchedNoise = () => {
    try {
        const audio = new Audio('/chime.mp3'); 
        audio.volume = 0.8;
        audio.play().catch(e => console.error("Audio playback error:", e));
    } catch (e) {
        console.error("Browser audio API issue:", e);
    }
};

// --- SCROLL WHEEL COMPONENT (The animated input) ---
const ScrollWheel = React.memo(({ label, value, max, increment, min, onChange }) => {
    const wheelRef = useRef(null);
    const itemHeight = 30; // Defined in CSS
    const initialOffset = 1000; // Large offset for infinite feel

    // Calculate the index corresponding to the current value
    const currentIndex = Math.floor((value - min) / increment);

    // This runs whenever the value changes to keep the scroll position synced
    useEffect(() => {
        const centerIndex = initialOffset + currentIndex;
        if (wheelRef.current) {
            // Smoothly scroll the wheel to the correct position
            wheelRef.current.scrollTop = centerIndex * itemHeight;
        }
    }, [currentIndex, min, increment]);


    const handleScroll = (e) => {
        // Debounce or throttle this in a production environment
        const element = e.currentTarget;
        const scrollIndex = Math.round(element.scrollTop / itemHeight);
        
        // Determine the actual value based on the centered index
        const newIndex = scrollIndex % (Math.floor((max - min) / increment) + 1);
        const newValue = (newIndex * increment) + min;

        // Only update if the value actually changed
        if (newValue !== value) {
            onChange(newValue);
        }
    };
    
    // Generate enough items to simulate infinite scroll visually (2000 items)
    const totalPossibleValues = Math.floor((max - min) / increment) + 1;
    const items = [];
    for (let i = 0; i < 2000; i++) {
        const val = ((i % totalPossibleValues) * increment) + min;
        items.push(
            <div key={i} className="scroll-item" style={{ height: itemHeight }}>
                {val}
            </div>
        );
    }

    return (
        <div className="input-group">
            <label className="input-label">{label}</label>
            <div 
                ref={wheelRef} 
                className="scroll-wheel"
                onScroll={handleScroll}
            >
                {items}
            </div>
            {/* The current value is visibly displayed by the centered scroll position */}
            <div className="value-overlay">{value}</div>
        </div>
    );
});


// --- MAIN APP COMPONENT ---

function App() {
    const [settings, setSettings] = useState({ rounds: 5, workTime: 60, restTime: 30 });
    const [timerState, setTimerState] = useState(WORKOUT_STATES.SETUP);
    const [timeRemaining, setTimeRemaining] = useState(0);
    const [currentRound, setCurrentRound] = useState(1);
    const intervalRef = useRef(null);

    // Logic to handle state transitions (WORK -> REST, REST -> WORK)
    const handleNextState = useCallback(() => {
        if (timerState === WORKOUT_STATES.PREP) {
            setTimerState(WORKOUT_STATES.WORK);
            setTimeRemaining(settings.workTime);

        } else if (timerState === WORKOUT_STATES.WORK) {
            if (currentRound < settings.rounds) {
                setTimerState(WORKOUT_STATES.REST);
                setTimeRemaining(settings.restTime);
            } else {
                setTimerState(WORKOUT_STATES.COMPLETE);
            }

        } else if (timerState === WORKOUT_STATES.REST) {
            const nextRound = currentRound + 1;
            setCurrentRound(nextRound);

            if (nextRound <= settings.rounds) {
                setTimerState(WORKOUT_STATES.WORK);
                setTimeRemaining(settings.workTime);
            } else {
                setTimerState(WORKOUT_STATES.COMPLETE);
            }
        }
    }, [timerState, settings, currentRound]);

    // The main timer loop (runs every second)
    useEffect(() => {
        if (timerState === WORKOUT_STATES.SETUP || timerState === WORKOUT_STATES.COMPLETE) {
            return;
        }

        intervalRef.current = setInterval(() => {
            setTimeRemaining(prevTime => {
                const newTime = prevTime - 1;

                // Sound Trigger: Plays on 3, 2, 1, 0 for all active timers
                if (newTime >= 0 && newTime <= 3) {
                    playHighPitchedNoise();
                }

                if (newTime < 0) {
                    clearInterval(intervalRef.current);
                    handleNextState();
                    return 0;
                }

                return newTime;
            });
        }, 1000);

        return () => clearInterval(intervalRef.current);
    }, [timerState, settings.rounds, currentRound, handleNextState]);

    const startWorkout = () => {
        // Clear any previous interval just in case
        clearInterval(intervalRef.current);

        if (settings.rounds > 0 && settings.workTime > 0) {
            setTimerState(WORKOUT_STATES.PREP);
            setTimeRemaining(PREP_TIME); // Start 5-second countdown
            setCurrentRound(1);
        }
    };

    // Helper to reset the app
    const resetWorkout = () => {
        clearInterval(intervalRef.current);
        setTimerState(WORKOUT_STATES.SETUP);
        setTimeRemaining(0);
        setCurrentRound(1);
    };

    // --- RENDER LOGIC ---

    if (timerState === WORKOUT_STATES.SETUP) {
        return (
            <div className="app setup-view">
                <h1 className="title">Interval Timer Setup</h1>
                <ScrollWheel 
                    label="Rounds (Max 15)"
                    value={settings.rounds}
                    min={1}
                    max={MAX_ROUNDS}
                    increment={1}
                    onChange={(val) => setSettings({...settings, rounds: val})}
                />
                <ScrollWheel 
                    label={`Work Time (Max ${MAX_TIME}s)`}
                    value={settings.workTime}
                    min={WORK_INCREMENT}
                    max={MAX_TIME}
                    increment={WORK_INCREMENT}
                    onChange={(val) => setSettings({...settings, workTime: val})}
                />
                <ScrollWheel 
                    label={`Rest Time (Max ${MAX_TIME}s)`}
                    value={settings.restTime}
                    min={REST_INCREMENT}
                    max={MAX_TIME}
                    increment={REST_INCREMENT}
                    onChange={(val) => setSettings({...settings, restTime: val})}
                />
                <button 
                    onClick={startWorkout} 
                    className="btn-start"
                    disabled={settings.rounds === 0 || settings.workTime === 0}
                >
                    Start Workout
                </button>
            </div>
        );
    }

    // Active Timer View
    const phaseLabel = {
        [WORKOUT_STATES.PREP]: 'GET READY',
        [WORKOUT_STATES.WORK]: 'WORK',
        [WORKOUT_STATES.REST]: 'REST',
        [WORKOUT_STATES.COMPLETE]: 'COMPLETE'
    }[timerState];

    const isComplete = timerState === WORKOUT_STATES.COMPLETE;
    const isPrep = timerState === WORKOUT_STATES.PREP;
    const isWorking = timerState === WORKOUT_STATES.WORK;

    return (
        <div className={`app timer-view ${isWorking ? 'work-phase' : ''} ${timerState === WORKOUT_STATES.REST ? 'rest-phase' : ''} ${isComplete ? 'complete-phase' : ''}`}>
            
            {!isComplete && (
                <div className="round-tracker">
                    {isPrep ? 'PREP' : `ROUND ${currentRound} OF ${settings.rounds}`}
                </div>
            )}

            <div className={`phase-label ${phaseLabel.toLowerCase()}`}>{phaseLabel}</div>

            <div className="main-timer">
                {isPrep ? timeRemaining : formatTime(timeRemaining)}
            </div>
            
            {isComplete && (
                <div className="complete-message">
                    WORKOUT COMPLETE! ðŸ’ª
                </div>
            )}

            <button onClick={resetWorkout} className="btn-reset">
                {isComplete ? 'New Workout' : 'Stop / Reset'}
            </button>
        </div>
    );
}

export default App;
